function segment = get_data(segment,m,to_str)% 取数据% 输入：1、一个 m行 2列 的二维数组 p%           2、二维数组 行数 m     %           2、字符数组 to_str    % 输出：1、存放数据的二维数组 pfor i=1:m    str = char(to_str{1,1}(i));    e=find(str==' ');        a = str( 1:(e(1)-1) );    a = str2double(a);        b = str((e(1)+1):end);    b = str2double(b);        segment(i,1)=a;    segment(i,2)=b;    if i<m        fprintf('P%d(%.2f, %.2f)\n',i,segment(i,1),segment(i,2));    else        fprintf('P%d(%.2f, %.2f)\n\n',i,segment(i,1),segment(i,2));    endendend